package pl.maciejwojs.ar00k.bestnotepadevercreaated.db.relationsimport androidx.room.Entityimport androidx.room.Index/** * Reprezentuje relację między plikami audio a notatkami. * * @property audioID ID pliku audio * @property noteID ID notatki */@Entity(primaryKeys = ["audioID", "noteID"], indices = [Index("audioID"), Index("noteID")])data class AudioFilesNotesCrossRef(    val audioID: Long,    val noteID: Long,)